'use client'
export default function BFGrid() {
  return (
    <div className="min-h-screen w-full bg-[#f8fafc] dark:bg-[#020617] relative flex items-center justify-center">
      {/* Light mode Top Fade Grid */}
      <div
        className="absolute inset-0 z-0 dark:hidden"
